import { ImageLayout, LineStyleType, TextAlign, TextStyle } from './renderer';

export interface BorderStyle {
    width: number;
    color: string;
    style: LineStyleType;
}

export interface FontStyle {
    fontSize: string;
    color: string;
    textAlign: TextAlign;
}

export interface CellStyle {
    background: {
        color?: string;
        image?: string;
        layout?: ImageLayout;
    };
    border: {
        borderTop?: BorderStyle;
        borderBottom?: BorderStyle;
        borderLeft?: BorderStyle;
        borderRight?: BorderStyle;
    };
    font: TextStyle;
    paddingLeft: number;
    paddingRight: number;
    paddingTop: number;
    paddingBottom: number;
}

export interface CellProps {
    type: string;
    style: CellStyle;
}

export interface TextCellProps extends CellProps {
    text: string;
}
